const handleEvent = Match.type<AppEvent>().pipe(
  Match.tag("UserCreated", (e) => notifyAdmin(e.userId)),
  Match.tag("UserDeleted", (e) => cleanupData(e.userId)),
  Match.tag("OrderPlaced", (e) => processOrder(e.orderId)),
  Match.exhaustive
)

