const handleUnknown = (input: unknown) => {
  if (typeof input === "object" && input !== null && (input as any)._tag === "Circle") {
    return `Valid circle`
  }
  return "Invalid shape"
}

// Using Data.TaggedError (doesn't work with Schema.is)
import { Data } from "effect"
class UserNotFound extends Data.TaggedError("UserNotFound")<{ userId: string }> {}

// Or checking error._tag directly
if (error._tag === "UserNotFound") {
  return defaultUser
}

example.good:
// Always use Schema.TaggedError for domain errors
class UserNotFound extends Schema.TaggedError<UserNotFound>()("UserNotFound", {
  userId: Schema.String
}) {}

// Works with Effect.catchTag
getUser(id).pipe(
  Effect.catchTag("UserNotFound", () => Effect.succeed(defaultUser))
)

// Works with Schema.is()
if (Schema.is(UserNotFound)(error)) {
  return defaultUser
}

// Works with Match.tag
Match.value(error).pipe(
  Match.tag("UserNotFound", (e) => Effect.succeed(defaultUser)),
  Match.exhaustive
)
